clear allclose all%Ej 10%ParametrosA1=1A2=1A3=-1f1=330f2=2*f1f3=3*f1fs = 20000; %Frecuencia de muestreoLs = 1; %Druacion de la senal en segundost = 1/fs:1/fs:Ls;  %Vector de tiempox1=A1*cos(2*pi*f1*t);x2=A2*cos(2*pi*f2*t);x3=A3*cos(2*pi*f3*t);x=x1+x2+x3;figure;subplot(4,1,1);plot(t,x1);axis([0 0.05 -2 2])subplot(4,1,2);plot(t,x2);axis([0 0.05 -2 2])subplot(4,1,3);plot(t,x3);axis([0 0.05 -2 2])subplot(4,1,4);plot(t,x);axis([0 0.05 -2 2])sound(x1,fs)%Ej 11Ls = 8; 		% Numero de segundos de la senal[xdet, fs] = audioread('ejemplo.wav'); t = 1/fs:1/fs:Ls;% Senal originalfigure;plot(t,xdet); title('Senal original');xlabel('tiempo');ylabel('amplitud');%sound(xdet,fs); %Descomente si quiere escuchar el audio% Senal con el doble de amplitudy1 = 2*xdet;figure;plot(t,y1);title('Senal con el doble de amplitud');xlabel('tiempo');ylabel('amplitud');%sound(y1,fs);  %Descomente si quiere escuchar el audio% Senal invertida en tiemploy2 = xdet(end:-1:1);figure; plot(t,y2);title('Senal invertida en tiempo');xlabel('tiempo');ylabel('amplitud');%sound(y3,fs); %Descomente si quiere escuchar el audio