clear all;close all;##Cargamos el audioLs = 8; 		% Numero de segundos de la senal[xdet, fs] = audioread('ejemplo.wav'); t = 1/fs:1/fs:Ls;L=length(t);##Calculamos la TFXdet=fftshift(fft(xdet)); f=-fs/2+fs/L:fs/L:fs/2; %eje frecuencia##Filtros%Paso bajo idealflow=500;  %Ejemplo de frecuencia de corte (Hz) Hdef_pb=zeros(1,L/2);Hdef_pb(1:flow/fs*L)=ones(1,flow/fs*L);Hdef_pb=[fliplr(Hdef_pb) Hdef_pb];%Paso alto idealfhigh=100; %Ejemplo de frecuencia de corte (Hz) Hdef_pa=ones(1,L/2);Hdef_pa(1:fhigh/fs*L)=zeros(1,fhigh/fs*L);Hdef_pa=[fliplr(Hdef_pa) Hdef_pa];%Combinasión##Hdef=Hdef_pb;Hdef=Hdef_pa;##Hdef=Hdef_pa+Hdef_pb;##Señal FiltradaYdef=Xdet'.*Hdef;ydet=ifft(fftshift(Ydef));        %Cálculo de la transformada de Fourier inversaydet=real(ydet);##Plotfigure;subplot(4,1,1)plot(t,xdet); title('Señal Original');xlabel('tiempo');ylabel('amplitud');##sound(xdet,fs); %Descomente si quiere escuchar el audiosubplot(4,1,2)plot(f,abs(Xdet)/max(abs(Xdet))); axis([-1000 1000 0 1.1])title('Módulo de TF X');xlabel('modulo');ylabel('frecuencia');hold on;plot(f,abs(Hdef),"g")             %filtrosubplot(4,1,3)plot(f,abs(Ydef)/max(abs(Ydef))); axis([-1000 1000 0 1.1])title('Módulo de TF Y');xlabel('modulo');ylabel('frecuencia');subplot(4,1,4)plot(t,ydet); title('Señal Filtrada');xlabel('tiempo');ylabel('amplitud');##sound(ydet,fs); %Descomente si quiere escuchar el audio